import React, { useState } from 'react';
import ImageSlider from './ImageSlider';
import CameraScreen from './CameraScreen';
import { useNavigate } from "react-router-dom";

const Layout = () => {
  const navigate = useNavigate();

  const handleActivateCamera = () => {
    navigate("/take-picture");
  };

  return (
    <div className="min-h-screen bg-white flex">
      {/* Lado Izquierdo: Image Slider */}
      <div className="w-1/2 p-4">
        <ImageSlider />
      </div>

      {/* Lado Derecho: Botón para ir a pantalla completa */}
      <div className="w-1/2 p-4 flex items-center justify-center">
      <div className="text-center mb-6">
        <h1 className="text-4xl font-bold text-orange-400">Sistema de Recomendaciones Turísticas</h1>
        <p className="text-orange-200 mt-2">
          Toma una foto de tu grupo y recibe recomendaciones personalizadas
        </p>
        <div className="relative w-full max-w-3xl aspect-video bg-black/50 rounded-xl overflow-hidden shadow-lg border border-orange-400">
        {photo ? (
          <img src={photo} alt="Captured" className="w-full h-full object-cover" />
        ) : (
          <video ref={videoRef} autoPlay className="w-full h-full object-cover" />
        )}
      </div>
      </div>
        <button
          onClick={handleActivateCamera}
          className="bg-orange-600 hover:bg-orange-700 text-white px-6 py-3 rounded-full flex items-center gap-2 transition-transform transform hover:scale-105"
        >
          Activar Cámara
        </button>
      </div>
    </div>
  );
};

  export default Layout;
