import React, { useState } from 'react';
import ImageSlider from './ImageSlider';
import CameraScreen from './CameraScreen';

const SplitLayout = () => {
  const [cameraActive, setCameraActive] = useState(false);

  const handleCameraStateChange = (isActive) => {
    setCameraActive(isActive);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-orange-100 via-orange-300">
      <div className={`
        flex transition-all duration-500 ease-in-out
        ${cameraActive ? 'scale-0 opacity-0 h-0' : 'scale-100 opacity-100'}
      `}>
        {/* Lado izquierdo - Image Slider */}
        <div className="w-1/2 p-4">
          <ImageSlider />
        </div>

        {/* Lado derecho - Camera Screen */}
        <div className="w-1/2 p-4">
          <CameraScreen 
            onCameraStateChange={handleCameraStateChange}
            layoutMode="split"
          />
        </div>
      </div>

      {/* Versi√≥n pantalla completa */}
      <div className={`
        fixed top-0 left-0 w-full h-full transition-all duration-500 ease-in-out
        ${cameraActive ? 'scale-100 opacity-100 z-10' : 'scale-95 opacity-0 -z-10 pointer-events-none'}
      `}>
        <div className="w-full h-full">
          <CameraScreen 
            onCameraStateChange={handleCameraStateChange}
            layoutMode="full"
            initialCameraState={true}
          />
        </div>
      </div>
    </div>
  );
};

export default SplitLayout;
