import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

const RecommendationScreen = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // Obtenemos la foto y otros datos del estado de la navegación
  const { photo } = location.state || {};

  const goBack = () => {
    navigate(-1); // Regresa a la pantalla anterior (CameraScreen)
  };

  return (
    <div className="h-screen bg-gray-900 text-white flex flex-col items-center p-6 space-y-6">
      {/* Header */}
      <div className="w-full flex items-center justify-between">
        <h1 className="text-2xl font-bold">Recommendations</h1>
        <button
          onClick={goBack}
          className="bg-red-500 px-4 py-2 rounded-lg text-white"
        >
          Back
        </button>
      </div>

      {/* Photo Display */}
      {photo && (
        <div className="w-full flex justify-center">
          <img
            src={photo}
            alt="Captured"
            className="rounded-lg shadow-lg w-full max-w-md"
          />
        </div>
      )}

      {/* Recommendation Details */}
      <div className="w-full max-w-4xl space-y-6">
        <div className="bg-gray-800 p-4 rounded-xl flex items-center space-x-4">
          <div className="bg-gray-700 p-3 rounded-lg">
            <img
              src="https://via.placeholder.com/50"
              alt="Diagnosis Icon"
              className="w-10 h-10"
            />
          </div>
          <div className="flex-1">
            <h3 className="text-lg font-semibold">Group Recommendation</h3>
            <p className="text-sm text-gray-400">
              Based on the detected group size, we suggest nearby attractions
              suited for {Math.floor(Math.random() * 10 + 1)} people.
            </p>
          </div>
          <button className="bg-orange-500 px-4 py-2 rounded-lg text-sm font-semibold">
            Details
          </button>
        </div>

        {/* Example for Lab Tests */}
        <div className="bg-gray-800 p-4 rounded-xl">
          <h4 className="text-sm text-gray-400">Other Suggestions</h4>
          <ul className="mt-2 text-sm text-gray-400 space-y-1">
            <li>- Visit the local museum</li>
            <li>- Try the food market nearby</li>
            <li>- Take a group photo at the city square</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default RecommendationScreen;
