import React, { useState } from 'react';
import ImageSlider from './ImageSlider';
import CameraScreen from './CameraScreen';

const Layout = () => {
  const [cameraActive, setCameraActive] = useState(false);

  // Función que pasaremos al CameraScreen para saber cuándo la cámara está activa
  const handleCameraStateChange = (isActive) => {
    setCameraActive(isActive);
  };

  return (
    <div className="min-h-screen bg-white">
      <div className={`
        flex transition-all duration-500
        ${cameraActive ? 'opacity-0 pointer-events-none' : 'opacity-100'}
      `}>
        {/* Lado izquierdo - Image Slider */}
        <div className={`
          w-1/2 p-4 transition-all duration-500
          ${cameraActive ? 'scale-0' : 'scale-100'}
        `}>
          <ImageSlider />
        </div>

        {/* Lado derecho - Camera Screen (versión miniatura) */}
        <div className={`
          w-1/2 p-4 transition-all duration-500
          ${cameraActive ? 'scale-0' : 'scale-100'}
        `}>
          <CameraScreen onCameraStateChange={handleCameraStateChange} />
        </div>
      </div>

      {/* Camera Screen (versión pantalla completa) */}
      <div className={`
        absolute top-0 left-0 w-full h-full transition-all duration-500
        ${cameraActive ? 'opacity-100 z-10' : 'opacity-0 pointer-events-none -z-10'}
      `}>
        <CameraScreen  fullScreen={cameraActive} />
      </div>
    </div>
  );
};

export default Layout;
