import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const ImageSlider = () => {
  // Dinámicamente importar todas las imágenes desde la carpeta 'images'
  const importAll = (r) => r.keys().map(r);
  const images = importAll(require.context("'../../public/images", false, /\.(png|jpe?g|webp)$/));

  // Crear las diapositivas automáticamente basadas en las imágenes
  const slides = images.map((img, index) => ({
    img: img.default, // Ruta de la imagen importada
    title: `Slide ${index + 1}`,
    description: `Descripción para la imagen ${index + 1}`,
  }));

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
  };

  return (
    <div className="w-full h-screen">
      <Slider {...settings}>
        {slides.map((slide, index) => (
          <div key={index} className="flex flex-col items-center h-screen">
            <img
              src={slide.img}
              alt={slide.title}
              className="w-full h-full object-cover"
            />
            <div className="absolute bottom-10 text-center bg-black/50 p-4 rounded-lg">
              <h2 className="text-white text-2xl font-bold">{slide.title}</h2>
              <p className="text-white text-sm">{slide.description}</p>
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default ImageSlider;
