import React, { useRef, useState } from "react";
import { Camera, RefreshCw, Check, Loader } from "lucide-react";
import { useNavigate } from "react-router-dom";

function CameraScreen() {
  const videoRef = useRef(null);
  const [photo, setPhoto] = useState(null);
  const [cameraStarted, setCameraStarted] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: {
          facingMode: "user",
          width: { ideal: 1280 },
          height: { ideal: 720 },
        },
      });
      videoRef.current.srcObject = stream;
      setCameraStarted(true);
    } catch (error) {
      console.error("Error accessing the camera:", error);
    }
  };

  const capturePhoto = () => {
    if (!cameraStarted || !videoRef.current) return;

    const canvas = document.createElement("canvas");
    const video = videoRef.current;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    const ctx = canvas.getContext("2d");
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
    const dataUrl = canvas.toDataURL("image/png");
    setPhoto(dataUrl);

    video.srcObject.getTracks().forEach((track) => track.stop());
    setCameraStarted(false);
  };

  const handleContinue = () => {
    setIsLoading(true);
    setTimeout(() => {
      navigate("/recommendation", { state: { photo } });
    }, 1500);
  };

  return (
    <div className="relative min-h-screen bg-gradient-to-b from-orange-100 via-orange-300 flex flex-col items-center justify-center overflow-hidden">
      {/* Background Animations */}
      <div className="absolute inset-0 z-0">
        {[
          "Quito",
          "Guayaquil",
          "Cuenca",
          "Baños",
          "Montañita",
          "Galápagos",
          "Otavalo",
          "Mindo",
          "Loja",
          "Ambato",
          "Tena",
        ].map((city, index) => {
          // Generar posiciones dentro del viewport pero alejadas del contenido central
          const side = Math.random() > 0.5 ? "left" : "right"; // Decidir si va a la izquierda o derecha
          const horizontalPosition = side === "left" ? "5%" : "85%"; // Mantenerlo visible a los lados
          const verticalPosition = `${Math.random() * 70 + 15}%`; // Entre 15% y 85% de la pantalla (para no tocar bordes extremos)

          return (
            <span
              key={index}
              className="absolute text-orange-500 text-opacity-30 text-4xl font-bold animate-float"
              style={{
                top: verticalPosition,
                left: horizontalPosition,
                animationDuration: `${5 + Math.random() * 5}s`,
                animationDelay: `${Math.random() * 3}s`,
              }}
            >
              {city}
            </span>
          );
        })}
      </div>

      {/* Content */}
      <div className="relative z-10 flex flex-col items-center justify-center">
        <div className="text-center mb-6">
          <h1 className="text-4xl font-bold text-orange-400">
            Sistema de Recomendaciones Turísticas
          </h1>
          <p className="text-orange-200 mt-2">
            Toma una foto de tu grupo y recibe recomendaciones personalizadas
          </p>
        </div>

        {/* Camera/Photo Container */}
        <div className="relative w-full max-w-3xl aspect-video bg-black/50 rounded-xl overflow-hidden shadow-lg border border-orange-400">
          {photo ? (
            <img src={photo} alt="Captured" className="w-full h-full object-cover" />
          ) : (
            <video
              ref={videoRef}
              autoPlay
              className="w-full h-full object-cover"
            />
          )}

          {cameraStarted && !photo && (
            <div className="absolute inset-0 border-2 border-dashed border-orange-300 m-4 rounded-lg pointer-events-none">
              <div className="absolute top-0 left-0 w-10 h-10 border-t-4 border-l-4 border-orange-400"></div>
              <div className="absolute top-0 right-0 w-10 h-10 border-t-4 border-r-4 border-orange-400"></div>
              <div className="absolute bottom-0 left-0 w-10 h-10 border-b-4 border-l-4 border-orange-400"></div>
              <div className="absolute bottom-0 right-0 w-10 h-10 border-b-4 border-r-4 border-orange-400"></div>
            </div>
          )}
        </div>

        {/* Controls */}
        <div className="flex justify-center gap-4 mt-8">
          {!cameraStarted && !photo && (
            <button
              onClick={startCamera}
              className="bg-orange-600 hover:bg-orange-700 text-white px-6 py-3 rounded-full flex items-center gap-2 transition-transform transform hover:scale-105"
            >
              <Camera className="w-5 h-5" />
              Activar Cámara
            </button>
          )}

          {cameraStarted && !photo && (
            <button
              onClick={capturePhoto}
              className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-full flex items-center gap-2 transition-transform transform hover:scale-105"
            >
              <Camera className="w-5 h-5" />
              Tomar Foto
            </button>
          )}

          {photo && !isLoading && (
            <div className="flex gap-4">
              <button
                onClick={() => setPhoto(null)}
                className="bg-red-600 hover:bg-red-700 text-white px-6 py-3 rounded-full flex items-center gap-2 transition-transform transform hover:scale-105"
              >
                <RefreshCw className="w-5 h-5" />
                Retomar
              </button>
              <button
                onClick={handleContinue}
                className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-full flex items-center gap-2 transition-transform transform hover:scale-105"
              >
                <Check className="w-5 h-5" />
                Continuar
              </button>
            </div>
          )}

          {isLoading && (
            <div className="flex items-center gap-3 text-orange-200">
              <Loader className="w-6 h-6 animate-spin" />
              Analizando imagen...
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default CameraScreen;
