import React from "react";
import { useLocation } from "react-router-dom";

function Recommendation() {
  const location = useLocation();
  const { photo } = location.state || {}; // Obtenemos la foto del estado de navegación

  return (
    <div className="h-screen flex flex-col items-center justify-center bg-gray-100">
      <h1 className="text-2xl font-bold mb-4">Recommendations</h1>
      {photo && (
        <img
          src={photo}
          alt="Captured"
          className="border rounded-lg shadow-lg w-3/4 h-auto mb-4"
        />
      )}
      <div className="bg-white p-4 rounded-lg shadow-lg w-3/4 text-center">
        <h2 className="text-lg font-bold">Your Recommendations</h2>
        <p className="mt-2">Example: Drink more water!</p>
      </div>
    </div>
  );
}

export default Recommendation;
