import React, { useState } from 'react';
import ImageSlider from './ImageSlider';
import CameraScreen from './CameraScreen';

const Layout = () => {
    const [cameraActive, setCameraActive] = useState(false);

    const handleCameraStateChange = (isActive) => {
      setCameraActive(isActive);
    };

    return (
      <div className="min-h-screen bg-white">
        {/* Vista inicial con ImageSlider y Cámara */}
        {!cameraActive && (
          <div className="flex">
            <div className="w-1/2 p-4">
              <ImageSlider />
            </div>
            <div className="w-1/2 p-4">
              <CameraScreen onCameraStateChange={handleCameraStateChange} />
            </div>
          </div>
        )}

        {/* Pantalla completa de la cámara */}
        {cameraActive && (
          <div className="absolute top-0 left-0 w-full h-full z-10">
            <CameraScreen onCameraStateChange={handleCameraStateChange} fullScreen={true} />
          </div>
        )}
      </div>
    );
  };

  export default Layout;
