import React, { useRef, useState } from "react";
import { Camera, RefreshCw, Check, Loader } from "lucide-react";
import { useNavigate } from "react-router-dom";
import ImageSlider from "./ImageSlider"; // Importar el componente ImageSlider

const CameraScreen = () => {
  const videoRef = useRef(null);
  const [photo, setPhoto] = useState(null);
  const [cameraStarted, setCameraStarted] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: {
          facingMode: "user",
          width: { ideal: 1280 },
          height: { ideal: 720 },
        },
      });
      videoRef.current.srcObject = stream;
      setCameraStarted(true);
    } catch (error) {
      console.error("Error accessing the camera:", error);
    }
  };

  const capturePhoto = () => {
    if (!cameraStarted || !videoRef.current) return;

    const canvas = document.createElement("canvas");
    const video = videoRef.current;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    const ctx = canvas.getContext("2d");
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
    const dataUrl = canvas.toDataURL("image/png");
    setPhoto(dataUrl);

    video.srcObject.getTracks().forEach((track) => track.stop());
    setCameraStarted(false);
  };

  const handleContinue = () => {
    setIsLoading(true);
    setTimeout(() => {
      navigate("/recommendation", { state: { photo } });
    }, 1500);
  };

  return (
    <div className="relative min-h-screen bg-gradient-to-b from-orange-100 via-orange-300 to-orange-400 flex flex-col items-center justify-center overflow-hidden">
      {!cameraStarted && <ImageSlider />} {/* Mostrar el slider si no se activa la cámara */}

      {/* Camera/Photo Container */}
      <div
        className={`relative w-full max-w-3xl ${
          cameraStarted ? "h-screen" : "h-1/2"
        } aspect-video bg-black/50 rounded-xl overflow-hidden shadow-lg border border-orange-400`}
      >
        {photo ? (
          <img src={photo} alt="Captured" className="w-full h-full object-cover" />
        ) : (
          <video
            ref={videoRef}
            autoPlay
            className="w-full h-full object-cover"
          />
        )}
      </div>

      {!cameraStarted && (
        <button
          onClick={startCamera}
          className="bg-orange-600 hover:bg-orange-700 text-white px-6 py-3 rounded-full flex items-center gap-2 transition-transform transform hover:scale-105 mt-6"
        >
          <Camera className="w-5 h-5" />
          Activar Cámara
        </button>
      )}
    </div>
  );
};

export default CameraScreen;
