import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { FaArrowLeft, FaArrowRight } from "react-icons/fa";

const ImageSlider = () => {
  const slides = [
    {
      img: "/images/pruebaIA2.webp",
      title: "Galápagos",
      description: "Explora las islas encantadas",
    },
    {
      img: "/images/pruebaIA2.webp",
      title: "Quito",
      description: "La capital en los Andes",
    },
    {
      img: "/images/pruebaIA2.webp",
      title: "Montañita",
      description: "Un paraíso para surfistas",
    },
  ];

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
    nextArrow: <FaArrowRight className="slick-arrow slick-next" />,
    prevArrow: <FaArrowLeft className="slick-arrow slick-prev" />,
  };

  return (
    <div className="w-full h-1/2">
      <Slider {...settings}>
        {slides.map((slide, index) => (
          <div key={index} className="flex flex-col items-center">
            <img
              src={slide.img}
              alt={slide.title}
              className="w-full h-64 object-cover rounded-xl"
            />
            <h2 className="text-lg font-bold mt-4">{slide.title}</h2>
            <p className="text-sm text-gray-600">{slide.description}</p>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default ImageSlider;
