import React, { useRef, useState } from "react";
import { Camera, RefreshCw, Check, Loader } from "lucide-react";
import { useNavigate } from "react-router-dom";

function CameraScreen() {
  const videoRef = useRef(null);
  const [photo, setPhoto] = useState(null);
  const [cameraStarted, setCameraStarted] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: {
          facingMode: "user",
          width: { ideal: 1920 },
          height: { ideal: 1080 },
        },
      });
      videoRef.current.srcObject = stream;
      setCameraStarted(true);
    } catch (error) {
      console.error("Error accessing the camera:", error);
    }
  };

  const capturePhoto = () => {
    if (!cameraStarted || !videoRef.current) return;

    const canvas = document.createElement("canvas");
    const video = videoRef.current;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    const ctx = canvas.getContext("2d");
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
    const dataUrl = canvas.toDataURL("image/png");
    setPhoto(dataUrl);

    video.srcObject.getTracks().forEach((track) => track.stop());
    setCameraStarted(false);
  };

  const handleContinue = () => {
    setIsLoading(true);
    setTimeout(() => {
      navigate("/recommendation", { state: { photo } });
    }, 1500);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-orange-900 via-orange-800 to-black text-white">
      <div className="container mx-auto px-6 py-12">
        <div className="max-w-4xl mx-auto">
          {/* Header */}
          <div className="text-center mb-10">
            <h1 className="text-4xl font-bold mb-4 text-orange-400">Sistema de Recomendaciones Turísticas</h1>
            <p className="text-orange-200 text-lg">
              Toma una foto de tu grupo y recibe recomendaciones personalizadas
            </p>
          </div>

          {/* Camera/Photo Container */}
          <div className="relative aspect-video bg-black/50 rounded-xl overflow-hidden shadow-xl border-4 border-orange-500 mb-8">
            {photo ? (
              <img src={photo} alt="Captured" className="w-full h-full object-cover" />
            ) : (
              <video ref={videoRef} autoPlay className="w-full h-full object-cover" />
            )}

            {/* Camera Guidelines */}
            {cameraStarted && !photo && (
              <div className="absolute inset-0 border-2 border-dashed border-orange-300 m-8 rounded-lg pointer-events-none">
                <div className="absolute top-0 left-0 w-8 h-8 border-t-2 border-l-2 border-orange-300"></div>
                <div className="absolute top-0 right-0 w-8 h-8 border-t-2 border-r-2 border-orange-300"></div>
                <div className="absolute bottom-0 left-0 w-8 h-8 border-b-2 border-l-2 border-orange-300"></div>
                <div className="absolute bottom-0 right-0 w-8 h-8 border-b-2 border-r-2 border-orange-300"></div>
              </div>
            )}
          </div>

          {/* Controls */}
          <div className="flex justify-center gap-4">
            {!cameraStarted && !photo && (
              <button
                onClick={startCamera}
                className="bg-orange-600 hover:bg-orange-700 text-white px-6 py-3 rounded-full flex items-center gap-2 transition-colors shadow-md"
              >
                <Camera className="w-5 h-5" />
                Activar Cámara
              </button>
            )}

            {cameraStarted && !photo && (
              <button
                onClick={capturePhoto}
                className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-full flex items-center gap-2 transition-colors shadow-md"
              >
                <Camera className="w-5 h-5" />
                Tomar Foto
              </button>
            )}

            {photo && !isLoading && (
              <div className="flex gap-4">
                <button
                  onClick={() => setPhoto(null)}
                  className="bg-red-600 hover:bg-red-700 text-white px-6 py-3 rounded-full flex items-center gap-2 transition-colors shadow-md"
                >
                  <RefreshCw className="w-5 h-5" />
                  Retomar
                </button>
                <button
                  onClick={handleContinue}
                  className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-full flex items-center gap-2 transition-colors shadow-md"
                >
                  <Check className="w-5 h-5" />
                  Continuar
                </button>
              </div>
            )}

            {isLoading && (
              <div className="flex items-center gap-3 text-orange-200">
                <Loader className="w-6 h-6 animate-spin" />
                Analizando imagen...
              </div>
            )}
          </div>

          {/* Instructions */}
          {cameraStarted && !photo && (
            <div className="mt-8 text-center text-orange-300">
              <p>Posiciona tu grupo dentro del marco</p>
              <p>Asegúrate de que los rostros sean visibles</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default CameraScreen;
